declare module '*.png' {
    const value: number;
    export = value;
  }
  
  declare module '*.jpg' {
    const value: number;
    export = value;
  }
  
  declare module '*.pdf' {
    const value: string;
    export default src;
  }
  declare module '*.ttf' {
    const value: string;
    export default src;
  }